        private fun replaceInReportBody(body: String, reason: String, email: String, postId: String, type: String): String {
            var bodyNew = body
            bodyNew = bodyNew.replace("insertreason", reason)
            bodyNew = bodyNew.replace("insertemail", email)
            bodyNew = bodyNew.replace("inserttype", type)
            bodyNew = bodyNew.replace("insertid", postId)
            return bodyNew
        }
        private fun replaceInFeedbackBody(body: String, reason: String, email: String): String {
